static void mapper165_ppu_clock(UINT16 address)
{
	if (mapper165_update) {
		mapper_map();
		mapper165_update = 0;
	}

	switch (address & 0x3ff8) {
		case 0x0fd0:
			mapper165_chrlatch(0) = 0;
			mapper165_update = 1;
			break;
		case 0x0fe8:
			mapper165_chrlatch(0) = 1;
			mapper165_update = 1;
			break;
		case 0x1fd0:
			mapper165_chrlatch(1) = 2;
			mapper165_update = 1;
			break;
		case 0x1fe8:
			mapper165_chrlatch(1) = 4;
			mapper165_update = 1;
			break;
	}
}

