static INT32 MemIndex()
{
	UINT8 *Next = AllMem;

	DrvPalette	= (UINT32*)Next; Next += 0x2000 * sizeof(UINT32);
	SMSPalette	= (UINT32*)Next; Next += 0x2000 * sizeof(UINT32);

	MemEnd		= Next;

	return 0;
}

static INT32 DrvDoReset()
{
	vdp_init();     // gets rid of crap on the screen w/GG
	render_init();  // ""
	system_reset();

	return 0;
}

INT32 SMSExit()
{
	ZetExit();
	GenericTilesExit();

	if (has_paddle) {
		has_paddle = 0;
		BurnTrackballExit();
	}

	if (has_gun) {
		has_gun = 0;
		BurnGunExit();
	}

	BurnFreeMemIndex();

	if (cart.rom) {
		BurnFree (cart.rom);
		cart.rom = NULL;
	}

	system_shutdown();

	return 0;
}

static inline void DrvClearOpposites(UINT8* nJoystickInputs)
{
	if ((*nJoystickInputs & 0x03) == 0x03) {
		*nJoystickInputs &= ~0x03;
	}
	if ((*nJoystickInputs & 0x0c) == 0x0c) {
		*nJoystickInputs &= ~0x0c;
	}
}

void DrvCalcPalette()
{
	for (INT32 i = 0; i < (240 * 0x20); i++)
	{
		INT32 r = (SMSPalette[i] >> 16) & 0xff;
		INT32 g = (SMSPalette[i] >>  8) & 0xff;
		INT32 b = (SMSPalette[i] >>  0) & 0xff;
		DrvPalette[i] = BurnHighCol(r, g, b, 0);
	}
}

INT32 SMSDraw()
{
	DrvCalcPalette();

	BurnTransferCopy(DrvPalette);

	if (has_gun) BurnGunDrawTargets();

	return 0;
}

INT32 SMSFrame()
{
	if (DrvReset) {
		DrvDoReset();
	}

	{
		// Inputs!
		input.system    = 0;
		input.pad[0]    = 0;
		input.pad[1]    = 0;
		input.analog[0] = 0x7F;
		input.analog[1] = 0x7F;

		if (has_paddle) {
			BurnTrackballConfig(0, AXIS_NORMAL, AXIS_NORMAL);
			BurnTrackballFrame(0, Analog[0], Analog[1], 0x01, 0xf);
			BurnTrackballUDLR(0, SMSJoy2[5], SMSJoy2[6], SMSJoy1[5], SMSJoy1[6], 7);
			BurnTrackballUpdate(0);
			input.analog[0] = BurnTrackballRead(0, 0);
			input.analog[1] = BurnTrackballRead(0, 1);
			BurnTrackballConfigStartStopPoints(0, 0, 0xff, 0, 0xff);
			if (SMSJoy1[7]) input.pad[0] |= INPUT_BUTTON1;
			if (SMSJoy2[7]) input.pad[1] |= INPUT_BUTTON1;
		} else {
			// Player 1
			if (has_gun) {
				BurnGunMakeInputs(0, Analog[0], Analog[1]);
				input.analog[0] = BurnGunReturnX(0);
				input.analog[1] = BurnGunReturnY(0) * 192 / 256;
				if (SMSJoy1[7]) input.pad[0] |= INPUT_BUTTON1; // gun trigger
			} else {
				if (SMSJoy1[3]) input.pad[0] |= INPUT_UP;
				if (SMSJoy1[4]) input.pad[0] |= INPUT_DOWN;
				if (SMSJoy1[5]) input.pad[0] |= INPUT_LEFT;
				if (SMSJoy1[6]) input.pad[0] |= INPUT_RIGHT;
				if (SMSJoy1[7]) input.pad[0] |= INPUT_BUTTON2;
				if (SMSJoy1[8]) input.pad[0] |= INPUT_BUTTON1;
				DrvClearOpposites(&input.pad[0]);
			}
			if (SMSJoy1[1]) input.system |= (IS_GG) ? INPUT_START : INPUT_PAUSE;

			// Player 2
			if (SMSJoy2[3]) input.pad[1] |= INPUT_UP;
			if (SMSJoy2[4]) input.pad[1] |= INPUT_DOWN;
			if (SMSJoy2[5]) input.pad[1] |= INPUT_LEFT;
			if (SMSJoy2[6]) input.pad[1] |= INPUT_RIGHT;
			if (SMSJoy2[7]) input.pad[1] |= INPUT_BUTTON2;
			if (SMSJoy2[8]) input.pad[1] |= INPUT_BUTTON1;
			DrvClearOpposites(&input.pad[1]);
		}

		gg_overscanmode = (SMSDips[0] & 0x08);
		vdp.no_spr_limit = (SMSDips[0] & 0x01);

	}

	if (pBurnDraw) {
		BurnTransferClear();
	}

	system_frame();

	if (pBurnDraw)
		SMSDraw();

	return 0;
}

