// *FIXED* T2 - Judgement Day - glitchy sprites in-game, noted to play the SMS version instead.

static INT32 load_rom()
{
    INT32 size;
	struct BurnRomInfo ri;

	BurnDrvGetRomInfo(&ri, 0);
	size = ri.nLen;

	// cart must be at least 1 page (16k)!
	if (size < 0x4000) {
		bprintf(0, _T("weird rom size, padding to 16k\n"));
		size = 0x4000;
	}
	if (size > 0x4000 && size < 0x8000) {
		bprintf(0, _T("weird rom size, padding to 32k\n"));
		size = 0x8000;
	}

	cart.rom = (UINT8 *)BurnMalloc((size < 0x100000) ? 0x100000 : size);
	if (BurnLoadRom(cart.rom + 0x0000, 0, 1)) 
		return 0;

    /* Take care of image header, if present */
	// TODO: I am not sure this is needed! it messes up some homebrew where they don't / 512 and have no header
    if(((size / 512) & 1) && ((BurnDrvGetHardwareCode() & HARDWARE_SMS_NO_CART_HEADER) != HARDWARE_SMS_NO_CART_HEADER))
	{
		bprintf(0, _T("Removed SMS Cart header.\n"));
        size -= 512;
        memmove(cart.rom, cart.rom + 512, size);
    }

    cart.pages = (size / 0x4000);
    cart.pages8k = (size / 0x2000);

    /* Assign default settings (US NTSC machine) */
    cart.mapper     = MAPPER_SEGA;
    sms.display     = DISPLAY_NTSC;
    sms.territory   = TERRITORY_EXPORT;

	/* Figure out game image type */
	if ((BurnDrvGetHardwareCode() & HARDWARE_PUBLIC_MASK) == HARDWARE_SEGA_GAME_GEAR) {
		sms.console = CONSOLE_GG;
	} else {
		sms.console = CONSOLE_SMS;
	}

	// Override mapper from hardware code
	switch (BurnDrvGetHardwareCode() & 0xff) {
		case HARDWARE_SMS_MAPPER_NONE:
			cart.mapper = MAPPER_NONE;
			break;

		case HARDWARE_SMS_MAPPER_CODIES: {
			cart.mapper = MAPPER_CODIES;
			break;
		}
		
		case HARDWARE_SMS_MAPPER_MSX: {
			cart.mapper = MAPPER_MSX;
			break;
		}
		
		case HARDWARE_SMS_MAPPER_MSX_NEMESIS: {
			cart.mapper = MAPPER_MSX_NEMESIS;
			break;
		}
		
		case HARDWARE_SMS_MAPPER_KOREA: {
			cart.mapper = MAPPER_KOREA;
			break;
		}

		case HARDWARE_SMS_MAPPER_KOREA8K: {
			cart.mapper = MAPPER_KOREA8K;
			break;
		}

		case HARDWARE_SMS_MAPPER_4PAK: {
			cart.mapper = MAPPER_4PAK;
			break;
		}

		case HARDWARE_SMS_MAPPER_XIN1: {
			cart.mapper = MAPPER_XIN1;
			break;
		}

		case HARDWARE_SMS_MAPPER_WONDERKID: {
			cart.mapper = MAPPER_WONDERKID;
			break;
		}

		default: {
			cart.mapper = MAPPER_SEGA;
			break;
		}
	}
	
	// Override GG SMS mode from hardware code
	if ((BurnDrvGetHardwareCode() & HARDWARE_SMS_GG_SMS_MODE) == HARDWARE_SMS_GG_SMS_MODE) {
		sms.console = CONSOLE_SMS;
	}
	
	// Override PAL mode from hardware code
	if ((BurnDrvGetHardwareCode() & HARDWARE_SMS_DISPLAY_PAL) == HARDWARE_SMS_DISPLAY_PAL) {
		sms.display = DISPLAY_PAL;
	}
	
	// Override Japanese territory from hardware code
	if ((BurnDrvGetHardwareCode() & HARDWARE_SMS_JAPANESE) == HARDWARE_SMS_JAPANESE) {
		sms.territory = TERRITORY_DOMESTIC;
	}

	if ((BurnDrvGetHardwareCode() & HARDWARE_SMS_CONTROL_PADDLE) == HARDWARE_SMS_CONTROL_PADDLE) {
		system_assign_device(PORT_A, DEVICE_PADDLE);
		system_assign_device(PORT_B, DEVICE_PADDLE);

		has_paddle = 1;
		BurnTrackballInit(2);

	} else
	if ((BurnDrvGetHardwareCode() & HARDWARE_SMS_CONTROL_PHASER) == HARDWARE_SMS_CONTROL_PHASER) {
		bprintf(0, _T("** HAS GUN **\n"));
		system_assign_device(PORT_A, DEVICE_PHASER);
		system_assign_device(PORT_B, DEVICE_PAD2B);

		has_gun = 1;
		BurnGunInit(1, true);

	} else {
		system_assign_device(PORT_A, DEVICE_PAD2B);
		system_assign_device(PORT_B, DEVICE_PAD2B);
	}

    return 1;
}

INT32 SMSInit()
{
	cart.rom = NULL;

	BurnAllocMemIndex();

	GenericTilesInit();

    if(load_rom() == 0)
	{
		bprintf(0, _T("Error loading SMS/GG rom!\n"));
		return 1;
	} else {
		bprintf(0, _T("%s @ "), (sms.console == CONSOLE_GG) ? _T("Game Gear") : _T("Master System"));
		bprintf(0, _T("%s - rom loaded ok!\n"), (sms.display == DISPLAY_NTSC) ? _T("NTSC / 60hz") : _T("Pal / 50hz"));
	}

	BurnSetRefreshRate((sms.display == DISPLAY_NTSC) ? 60.0 : 50.0);

	/* Set up bitmap structure */
    memset(&bitmap, 0, sizeof(bitmap_t));
    bitmap.width  = 256;
    bitmap.height = 192;
    bitmap.depth  = 16;
    bitmap.granularity = 2;
    bitmap.pitch  = bitmap.width * bitmap.granularity;
    bitmap.data   = (UINT8 *)pTransDraw;
    bitmap.viewport.x = 0;
    bitmap.viewport.y = 0;
    bitmap.viewport.w = 256;
    bitmap.viewport.h = 192;

    snd.fm_clock = (sms.display == DISPLAY_PAL) ? CLOCK_PAL : CLOCK_NTSC;
    snd.psg_clock = (sms.display == DISPLAY_PAL) ? CLOCK_PAL : CLOCK_NTSC;
	sms.use_fm = (SMSDips[0] & 0x04);

    system_init();

	memset(cart.sram,    0, sizeof(cart.sram));

	system_reset();

	return 0;
}

static INT32 sramInit()
{
	bprintf(0, _T("**  SMS/GG with SRAM.\n"));

	INT32 rc = SMSInit();

	if (!rc) {
		cart.use_sram = 1;
	}

	return rc;
}

static INT32 rbislandInit()
{
	INT32 rc = SMSInit();

	if (rc == 0 && cart.rom[0x7334] == 0xe5) {
		bprintf(0, _T("**  SMS HOTFIX: Applying Rainbow Islands \"ending-crash\" fix.\n"));
		cart.rom[0x7334] = 0xe1;
	}

	return rc;
}

static void system_load_state()
{
	if(cart.mapper == MAPPER_MSX || cart.mapper == MAPPER_MSX_NEMESIS) {
		if (cart.fcr[0]) sms_mapper8k_w(0, cart.fcr[0]);
		if (cart.fcr[1]) sms_mapper8k_w(1, cart.fcr[1]);
		if (cart.fcr[2]) sms_mapper8k_w(2, cart.fcr[2]);
		if (cart.fcr[3]) sms_mapper8k_w(3, cart.fcr[3]);
	} else {
		if(cart.mapper == MAPPER_KOREA8K) {
			if (cart.fcr[0]) sms_mapper8kvirt_w(0, cart.fcr[0]);
			if (cart.fcr[1]) sms_mapper8kvirt_w(1, cart.fcr[1]);
			if (cart.fcr[2]) sms_mapper8kvirt_w(2, cart.fcr[2]);
			if (cart.fcr[3]) sms_mapper8kvirt_w(3, cart.fcr[3]);
		} else if (cart.mapper != MAPPER_XIN1 && cart.mapper != MAPPER_NONE && cart.mapper != MAPPER_WONDERKID) {
			sms_mapper_w(0, cart.fcr[0]);
			sms_mapper_w(1, cart.fcr[1]);
			sms_mapper_w(2, cart.fcr[2]);
			sms_mapper_w(3, cart.fcr[3]);
		}

		invalidate_bg_pattern_cache();
		viewport_check(); // refresh vdp
	}
}

INT32 SMSScan(INT32 nAction, INT32 *pnMin)
{
	if (pnMin) {
		*pnMin = 0x029708;
	}

	if (nAction & ACB_VOLATILE) {
		ZetScan(nAction);
		SN76496Scan(nAction, pnMin);

		SCAN_VAR(vdp);
		SCAN_VAR(sms);
		SCAN_VAR(cart.fcr);

		ScanVar(SMSPalette, 0x2000 * sizeof(UINT32), "LinePalette");

		if (has_gun) {
			BurnGunScan();
		}

		if (sms.use_fm) // put it down here so we keep compatibility with non-fm states.
			BurnYM2413Scan(nAction, pnMin);

		if (nAction & ACB_WRITE) {
			ZetOpen(0);
			system_load_state();
			ZetClose();
		}
	}

	if (nAction & ACB_NVRAM && cart.use_sram) {
		if (nAction & ACB_WRITE) {
			bprintf(0, _T("SRAM LOAD.\n"));
		} else {
			bprintf(0, _T("SRAM SAVE.\n"));
		}
		ScanVar(cart.sram, 0x8000, "SRAM");
	}

	return 0;
}

INT32 SMSGetZipName(char** pszName, UINT32 i)
{
	static char szFilename[MAX_PATH];
	char* pszGameName = NULL;

	if (pszName == NULL) {
		return 1;
	}

	if (i == 0) {
		pszGameName = BurnDrvGetTextA(DRV_NAME);
	} else {
		pszGameName = BurnDrvGetTextA(DRV_PARENT);
	}

	if (pszGameName == NULL || i > 1) {
		*pszName = NULL;
		return 1;
	}

	// remove sms_
	memset(szFilename, 0, MAX_PATH);
	for (UINT32 j = 0; j < (strlen(pszGameName) - 4); j++) {
		szFilename[j] = pszGameName[j+4];
	}

	*pszName = szFilename;

	return 0;
}

